import React, { useEffect, useState } from 'react';
import { SiWhatsapp } from "react-icons/si";
import { useParams } from 'react-router-dom';
import api from '../services/api';

import '../styles/components/list.css';

interface Orphanage {
    id: number;
    name: string;
    instructions: string;
    description: string;
    opening_hours: string;
    images: Array<{
        id: number;
        url: string;
      }>
}

interface OrphanageParams {
    id: string;
  }

function Profile(){
    const params = useParams<OrphanageParams>();
    const [orphanage, setOrphanage] = useState<Orphanage>();
  
    
  
      // execute a função quando a variavel no [] tiver alterado
      useEffect(() => {
          
          let index = 8;
          api.get(`orphanages/${index}`).then(response => {
          setOrphanage(response.data);
        
        });
      }, [params.id]);

      console.log(orphanage)
  
      if (!orphanage) {
        return <p>Carregando...</p>
      }

      return(

  

    <article className="list">

        <header>
            <img src={orphanage.images[0].url} alt="Foto do orfanato"/>
            <strong>
                 <h5>Instituição:</h5>
                    <h1>{orphanage.name}</h1>
                    </strong>
             <div className="">
                

                            
                    <h5>Instruções:
                      <span>{orphanage.instructions}</span></h5>
                   
                     <br/>
             </div>
        </header>
             <p>Horário
                 <br/>
                {orphanage.opening_hours}
             </p>

             <footer>
                 <p>Horário:
                     <strong>{orphanage.opening_hours}</strong>
                 </p>
                 <button type="button">
                      <SiWhatsapp />
                     <p> Entrar em contado </p>
                 </button>
             </footer>
             <div className="space">
                 __________________________________________________________________________
             </div>
        
        <header>
            <img src={orphanage.images[0].url} alt="Foto do orfanato"/>
             <div className="">
                 <strong>
                    {orphanage.name}

                      <span>Html </span>
                     </strong>
             </div>
        </header>
             <p>Designer grafico
                 <br/> <br/>
                {orphanage.name}
             </p>

             <footer>
                 <p>Preço por Hora
                     <strong>R$ 2,50</strong>
                 </p>
                 <button type="button">
                      <SiWhatsapp />
                     <p> Entrar em contado </p>
                 </button>
             </footer>
             <div className="space">
                 __________________________________________________________________________
             </div>
             
    </article>

    
        
      )
    }

        

export default Profile;
