import React, { ReactNode } from 'react';

export interface Name {
  name: string
  // o overlay serve pra se sobrepor
  overlayNode: ReactNode
  // ela se referencia a nome 1, nome 2...
  sectionRef: React.RefObject<HTMLElement>
}

interface ModelsContext{
  wrapperRef: React.RefObject<HTMLElement>
  // pega o nome
  registeredNames: Name[];
  // define o nome
  registerName: (model: Name) => void
  // pra fazer o nome desaparecer
  unregister: (textName: string) => void
  // faz tipo um CRUD no front-end onde recebe o nome pelo Nome
  getName: (textName: string) => Name | null
}

export default React.createContext<ModelsContext>(
  {} as ModelsContext
)
