import styled from 'styled-components'
import { motion } from 'framer-motion'

export const Main = styled.main`
  .first {
    background: red;
    overflow: hidden;

    .offset {
      width: 100%;
      height: 100%;
    }

    .a {
      background: #f5f1ea;
      height: 100%;
      display: flex;
      /* joga o item para baixo pra ele subir */
      align-items: flex-end;

      .left-side {
        width: 50%;
        height: 100%;
        background: #ceecec;
      }
      .right-side {
        width: 50%;
        height: 100%;

        .right-image {
          background: #000;
          width: 100%;
          height: 100%;
        }
      }
    }
    .b {
      width: 100%;
      height: 100%;
      background: #582534;
      content: '3';
    }
    .c {
      width: 100%;
      height: 100%;
      background: #0a7397;
    }
  }
  .second {
    background: green;
  }
  .third {
    background: yellow;
  }
  .fourth {
    background: blue;
  }
`

export const Section = styled.div`
  position: relative;
`
// ele é um motion.div pra poder ter acesso aos comandos
export const Sticky = styled(motion.div)`
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
`
