import React, {useState, FormEvent} from 'react'
import { useHistory } from "react-router-dom";
import {Link} from 'react-router-dom';

import api from "../services/api";

import '../styles/pages/form.css' 



import Input from '../components/Input';
import PageHeader from '../components/Header';


function Form(){
  // pega o historico do http//
  const history = useHistory();

  const [nome, setNome]               = useState("");
  const [cep, setCep]                 = useState("");
  const [renda, setRenda]             = useState("");
  const [dependentes, setDependentes] = useState("");


  async function handleSubmit(event: FormEvent) {

    event.preventDefault();

    const data = new FormData();

    data.append("nome",        nome);
    data.append("cep",         cep);
    data.append("renda",       renda);
    data.append("dependentes", dependentes);

    // faz o post na API
    await api.post("orphanages", data);

    alert("Cadastro com Sucesso!");
    // encaminha para outra pagina
    history.push("/dashboard");
  }


    return(
        <div id="form-page">
            <div className="Header">
            
            <PageHeader title="Digite os valores:"/>
            </div>


       <article className="form-item">
         <form onSubmit={handleSubmit} >
        <header>

           
        </header>
             
          <main>
              <fieldset>
              <legend>Seus Dados</legend>

          <Input alt="Digite seu nome completo"          type="text"     name="Nome" label="Nome Completo" />
          <Input alt="Digite seu CEP"                    type="number"   name="CEP" label="CEP" />
          <Input alt="Digite sua renda mensal"           type="number"   name="Renda mensal" label="Renda mensal" />
          <Input alt="Digite o número de dependentes"    type="number"   name="Número de dependentes" label="Número de dependentes" />



            <footer>
          <p>
            <img alt="Aviso Importante" />
            Importante <br />
            Preencha todos os dados
          </p>
          <button type="button" >Salvar cadastro</button>

          </footer>
          </fieldset>


      </main>
      </form>
    </article> 
    </div>
    );

}

export default Form;
