import React, { useCallback, useRef, useState } from 'react';

import ModelsContext, { CarModel} from '../ModelsContext/ModelsContext';

import { Container } from './style';

const ModelsWrapper: React.FC = ({children}) => {
 
  const wrapperRef = useRef<HTMLDivElement>(null)

  // cria o array com as seções de texto
  const [registeredModels, setRegisteredModels] = useState<CarModel[]>([])

  // registra o texto
  const registerModel = useCallback((model: CarModel) => {
    setRegisteredModels(state => [...state, { ...model }])
  }, [])

  // tira o model
  const unregisterModel = useCallback((modelName: string) => {
    setRegisteredModels(state =>
      state.filter(model => model.modelName !== modelName)
    )
  }, [])

  const getModelByName = useCallback(
    (modelName: string) => {
      return registeredModels.find(item => item.modelName === modelName) || null
    },
    [registeredModels]
  )

  return (
    <ModelsContext.Provider
      value={{
        wrapperRef,
        registeredModels,
        registerModel,
        unregisterModel,
        getModelByName
      }}
    >
      <Container ref={wrapperRef}>
        <OverlaysRoot>
          {registeredModels.map(item => (
            <ModelOverlay key={item.modelName} model={item}>
              {item.overlayNode}
            </ModelOverlay>
          ))}
        </OverlaysRoot>

        {children}
      </Container>
    </ModelsContext.Provider>
  );
}

export default ModelsWrapper;
