import React, { useEffect} from 'react';
import { Link } from 'react-router-dom';
import { FiPlus, FiArrowRight, FiList, FiArrowLeft } from 'react-icons/fi';


// adiconando o map
import { Map, TileLayer, Marker, Popup } from 'react-leaflet'
import Leaflet from 'leaflet';
import 'leaflet/dist/leaflet.css'


import mapMarkerImg from '../images/map-marker.svg';
import '../styles/pages/orphanage-map.css'
import api from '../services/api';
import { useState } from 'react';

const mapIcon = Leaflet.icon({
    iconUrl: mapMarkerImg,

    iconSize: [58, 68],
    iconAnchor: [29, 68],
    popupAnchor: [170, 2]
});

interface Orphanage {
    id: number;
    latitude: number;
    longitude: number;
    name: string;
}

function OrphanagesMap(){
    const [orphanages, setOrphanages] = useState<Orphanage[]>([]);

    // execute a função quando a variavel no [] tiver alterado
    useEffect(() => {
        api.get('orphanages').then(response => {
            setOrphanages(response.data);
        })
    }, []);

    return(
        <div className="" id="page-map">
            <aside>
                <header>
                    <img src={mapMarkerImg} alt="Mappy"/>

                    <h2>Escolha um orfanato no mapa</h2>
                    <p>Muitas crianças estão esperando sua visita :) </p>
                </header>

                <footer>
                    <strong>Belo Horizonte</strong> <br/>
                    <span>Minas Gerais</span>
                </footer>

                <Link to="/app" className="enter-list">
                     <FiList size={26} color="rgba(0,0,0,0.6)"/>
                </Link>

            </aside>

            <Map
                center={[-19.9023386,-44.1041366]}
                zoom={11}
                style={{ width: '100%', height: '100%' }}
            >

                {/* <TileLayer url="https://a.tile.openstreetmap.org/{z}/{x}/{y}.png" /> */}
                <TileLayer 
                url={`https://api.mapbox.com/styles/v1/mapbox/dark-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}
                />

              {orphanages.map(orphanage => {
                  return(
                    <Marker
                    icon={mapIcon}
                    position={[orphanage.latitude,orphanage.longitude]}
                    key={orphanage.id }
                >
                
                <Popup closeButton={false} minWidth={240} maxWidth={240} className="map-popup">
                    {orphanage.name}
                    <Link to={`/orphanages/${orphanage.id}`}>
                        <FiArrowRight size={20} color="#FFF" />
                    </Link>
                </Popup>
                
                </Marker>
              )})}

            </Map>


            <Link to="/orphanages/create" className="create-orphanage">
                <FiPlus size={32} color="fff" />
            </Link>

        </div>
    );
}
console.log(process.env.REACT_APP_MAPBOX_TOKEN);
export default OrphanagesMap;
