import React, { ChangeEvent, FormEvent } from "react";
import { Map, Marker, TileLayer } from 'react-leaflet';
import { LeafletMouseEvent } from 'leaflet'
import {  FiPlus } from "react-icons/fi";


import '../styles/pages/create-orphanage.css';

import Sidebar from "../components/Sidebar";
import mapIcon from "../utils/mapIcon";
import { useState } from "react";
import e from "express";



export default function CreateOrphanage() {

  const [position, setPosition] = useState({ latitude: 0, longitude: 0 })

  // formulario:
  const [name, setName]                          = useState('');
  const [about, setAbout]                        = useState('');
  const [instructions, setInstructions]          = useState('');
  const [opening_hours, setOpeningHours]         = useState('');
  const [open_on_weekends, setOpenonweekends]    = useState(true);
  const [images, setImages]                      = useState<File[]>([]);
  const [previewImages, setPreviewImages]        = useState<string[]>([]);


  function handleMapClick(event: LeafletMouseEvent){
    // console.log(event.latlng);
    const {lat, lng} = event.latlng;

    setPosition({
      latitude: lat,
      longitude: lng,

    });
  }

  function handleSelectimages(event: ChangeEvent<HTMLInputElement>){
    if (!event.target.files){
      return;
    }

    const selectedImages = Array.from(event.target.files);

    setImages(selectedImages);

     const selectedImagesPreview = selectedImages.map(image => {
       return URL.createObjectURL(image);
     });

     setPreviewImages(selectedImagesPreview);


    // console.log(event.target.files);
  }

  function handleSubmit(event: FormEvent){
    event.preventDefault();


    const { latitude, longitude } = position;


    console.log({
      name,
      about,
      latitude, 
      longitude,
      instructions,
      opening_hours,
      open_on_weekends,

    })
  }

  return (
    <div id="page-create-orphanage">

      <Sidebar/>

      <main>
        <form className="create-orphanage-form">
          <fieldset>
            <legend>Dados</legend>

            <Map 
              center={[-19.9172987, -43.9345593]} 
              style={{ width: '100%', height: 280 }}
              zoom={15}
              onclick={handleMapClick}
            >
              <TileLayer 
                url={`https://api.mapbox.com/styles/v1/mapbox/dark-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}
              />

              { position.latitude != 0 
              // pra fazer um if que só vai fazer algo se for positivo e o else nada só coloar &&
              &&    (
                     <Marker 
                     interactive={false} 
                     icon={mapIcon} 
                     position={[position.latitude, position.longitude ]} />
               )}

          </Map>

            <div className="input-block">
              <label htmlFor="name">Nome</label>
              <input id="name"
               value={name} onChange={event => setName(event.target.value)} />
            </div>

            <div className="input-block">
              <label htmlFor="about">Sobre <span>Máximo de 300 caracteres</span></label>
              <textarea id="name" maxLength={300} 
               value={about} onChange={event => setAbout(event.target.value)}/>
            </div>


            <div className="input-block">
              <label htmlFor="images">Fotos</label>

              <div className="images-container">

                {previewImages.map(image =>{
                  return (
                    <img src={image} alt={name}/>
                  )
                })}

              <label htmlFor="image[]" className="new-image">

                    <FiPlus size={24} color="#15b6d6" />

              </label>


              </div>

              <input multiple onChange= {handleSelectimages} type="file" id="image[]"/>

            </div>
          </fieldset>

          <fieldset>
            <legend>Visitação</legend>

            <div className="input-block">
              <label htmlFor="instructions">Instruções</label>
              <textarea id="instructions"
               value={instructions} onChange={event => setInstructions(event.target.value)} />
            </div>

            <div className="input-block">
              <label htmlFor="opening_hours">Horário de funcionamento</label>
              <input id="opening_hours"
               value={opening_hours} onChange={event => setOpeningHours(event.target.value)} />
            </div>

            <div className="input-block">
              <label htmlFor="open_on_weekends">Atende fim de semana</label>

              <div className="button-select">
                <button type="button" 
                 className={open_on_weekends ? 'active' : ''}
                 onClick= {() => setOpenonweekends(true)}>
                  Sim
                  </button>
                <button type="button"
                className={!open_on_weekends ? 'active' : ''}
                onClick= {() => setOpenonweekends(false)}>
                  Não
                  </button>
              </div>
            </div>
          </fieldset>

          <button className="confirm-button" type="submit">
            Confirmar
          </button>
        </form>
      </main>
    </div>
  );
}

// return `https://a.tile.openstreetmap.org/${z}/${x}/${y}.png`;
