import React, { useEffect, useState } from 'react';
import { SiWhatsapp } from "react-icons/si";
import { useParams } from 'react-router-dom';
import api from '../services/api';

import '../styles/components/list.css';

interface Orphanage {
    id: number;
    name: string;
    description: string;
     index: 8;
}

interface OrphanageParams {
    id: string;
  }

function Profile(){
    const params = useParams<OrphanageParams>();
    const [orphanage, setOrphanage] = useState<Orphanage>();
  
    debugger
  
      // execute a função quando a variavel no [] tiver alterado
      useEffect(() => {
          debugger
          let index =8
          api.get(`orphanages/${index}`).then(response => {
          setOrphanage(response.data);
        
        });
      }, [params.id]);

      console.log(orphanage)
  
      if (!orphanage) {
        return <p>Carregando...</p>
      }

      return(

  

            <article className="list">

        <header>
            <img src="https://avatars0.githubusercontent.com/u/49418565?s=460&u=caa6add820c3e5842ba889673008612e15261857&v=4" alt="Juan Andrade"/>
             <div className="">
                 <strong>
                    {orphanage.name}

                      <span>Html </span>
                     </strong>
             </div>
        </header>
             <p>Designer grafico
                 <br/> <br/>
                {orphanage.name}
             </p>

             <footer>
                 <p>Preço por Hora
                     <strong>R$ 2,50</strong>
                 </p>
                 <button type="button">
                      <SiWhatsapp />
                     <p> Entrar em contado </p>
                 </button>
             </footer>
             <div className="space">
                 __________________________________________________________________________
             </div>
             
    </article>
        
      )
    }

        

export default Profile;
