import React, { useEffect } from 'react'
import api from '../../../services/api';

import { Container, Heading } from './styles'

import { useParams } from "react-router-dom";

// dentro da interface ela armazena oque vai ter na head de string
interface Props {
  label: string
  description: string
}

// define o id do http
interface InfoParams {
  id: string;
}


const params = useParams<InfoParams>();

useEffect(() => {
  api.get(`infos/${params.id}`).then((response) => {
    setInfo(response.data);
  });
}, [params.id]);

const DefaultOverlayContent: React.FC<Props> = ({ label, description }) => {
  return (
    <Container>
      <Heading>
        <div className="text">
        <h1>{label}</h1>
        <h2>{description}</h2>
        </div>
      </Heading>

    </Container>
  )
}

export default DefaultOverlayContent
