import styled from 'styled-components'
import { motion } from 'framer-motion'

export const Main = styled.main`
*{
  color:red;
}

  .first {
    background: #000;
    overflow: hidden;

    .offset {
      width: 100%;
      height: 100%;
    }

    .a {
      background: #f5f1ea;
      height: 100%;
      display: flex;
      /* joga o item para baixo pra ele subir */
      align-items: flex-end;

      .alinhar{
        display: block;
        
      }

      .alinhar img{
        width: 100%;
      }

      .left-side {
        width: 50%;
        height: 100%;
        background: #000;
      }
      .right-side {
        width: 50%;
        height: 100%;
        /* lado direito do começo do quadro com a imagem */
        .right-image {
          background: #000;
          width: 100%;
          height: 100%;
        }
      }
    }
    /* second */
    .b {
      width: 100%;
      height: 100%;
      background: #fff;
    }
   
  }
  .second {
    background: #000;
  }
`

export const Section = styled.div`
  position: relative;
`
// ele é um motion.div pra poder ter acesso aos comandos
export const Sticky = styled(motion.div)`
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
`
