import styled from 'styled-components'

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  flex: 1;
`

export const Heading = styled.div`
  margin-top: 4.5rem;
  text-align: center;
  font-family: Nunito;

  > h1 {
    font-weight: 500;
    font-size: 40px;
    line-height: 48px;
    color: #323232
  }
  > h2 {
    font-weight: normal;
    font-size: 10px;
    line-height: 20px;
    color: #e1f4f3;
  }

`
