import React from 'react'
import { useViewportScroll, useTransform, motion } from 'framer-motion'

import { Sticky } from '../pages/Dashboard/styles'

const FirstAndSecond: React.FC = () => {
  const { scrollYProgress } = useViewportScroll()

  const frameOpacity = useTransform(scrollYProgress, [0.196, 0.198], [0, 1])
  const frameScale = useTransform(scrollYProgress, [0.558, 0.627], [0.511, 0.8])

  return (
    <Sticky className="second">
      <First />

      <motion.div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '100vh',
          borderRadius: '4px',
          border: '4px solid #fff',
          opacity: frameOpacity,
          scale: frameScale
        }}
      />
    </Sticky>
  )
}

const First: React.FC = () => {
  const { scrollYProgress } = useViewportScroll()
  const firstRadius = useTransform(
    scrollYProgress,
    [0.198, 0.264, 0.558, 0.627],
    [0, 4, 4, 0]
  )
  const firstScale = useTransform(
    scrollYProgress,
    [0.198, 0.264, 0.558, 0.627],
    [1, 0.511, 0.511, 1]
  )
  const leftSideHeight = useTransform(
    scrollYProgress,
    [0, 0.058],
    ['20vh', '100vh']
  )
  const rightSideScale = useTransform(
    scrollYProgress,
    [0.047, 0.093],
    [0, 0.511]
  )
  const rightSideY = useTransform(
    scrollYProgress,
    [0.047, 0.093],
    ['58vh', '0vh']
  )
  const offsetY = useTransform(
    scrollYProgress,
    [0.328, 0.397, 0.461, 0.53],
    ['0%', '-100%', '-100%', '-200%']
  )

  return (
    <Sticky
      className="first"
      style={{
        borderRadius: firstRadius,
        scale: firstScale
      }}
    >
      <motion.div
        className="offset"
        style={{
          y: offsetY
        }}
        
      >
        <div className="a">
            <h1>teste</h1>
          <motion.div
            className="left-side"
            style={{
              height: leftSideHeight
            }}
          />
          <h1>Esse é o quadro vermelho da segunda scrolagem</h1>
          <div className="right-side">
            <motion.div
              className="right-image"
              style={{
                y: rightSideY,
                scale: rightSideScale
              }}
            />
            <h1>Esse é o quadro vermelho da terceira scrolagem</h1>
          </div>
        </div>

        <div className="b" />
        <div className="c" />
      </motion.div>
    </Sticky>
  )
}

export default FirstAndSecond
